import { useState } from "react";
import styles from "./UserInput.module.css";
const UserInput = () => {

  const initialUserInput = {
    'current-saving' : 10000,
    'yearly-contribution' : 1200,
    'expected-return' : 7,
    'duration' : 10
  }

  const[userInput, setUserInput] = useState(initialUserInput)

  const submitHandler = (event) => {
    event.preventDefault();
  };

  const resetHandler = () => {
    setUserInput(initialUserInput)
  };

  const inputChangeHandler = (input, value) => {
    setUserInput((prevInput) => {
      return {
        ...prevInput,
        [input] : value
      }
    })
  };

  return (
    <form className={styles.form} onSubmit={submitHandler}>
      <div className={styles["input-group"]}>
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input
            onChange={(event) =>
              inputChangeHandler("current-saving", event.target.value)
            }
            value={userInput['current-saving']}
            type="number"
            id="current-savings"
          />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input type="number" id="yearly-contribution"  onChange={(event) =>
              inputChangeHandler("yearly-contribution", event.target.value)
            }/>
        </p>
      </div>
      <div className={styles["input-group"]}>
        <p>
          <label htmlFor="expected-return">
            Expected Interest (%, per year)
          </label>
          <input type="number" id="expected-return"  onChange={(event) =>
              inputChangeHandler("expected-return", event.target.value)
            }/>
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input type="number" id="duration"  onChange={(event) =>
              inputChangeHandler("duration", event.target.value)
            }/>
        </p>
      </div>
      <p className={styles.actions}>
        <button
          type="reset"
          className={styles.buttonAlt}
          onSubmit={resetHandler}
        >
          Reset
        </button>
        <button type="submit" className={styles.button}>
          Calculate
        </button>
      </p>
    </form>
  );
};

export default UserInput;
