import styles from "./ResultsTable.module.css";

const formatter = new Intl.NumberFormat("en-US", {
  style: "currency",
  currency: "INR",
  minimumFractionDigits: 2,
  maximumFractionDigits: 2,
});


const ResultsTable = (props) => {
  return (
    <table className={styles.result}>
      <thead>
        <tr>
          <th>Year</th>
          <th>Total Savings</th>
          <th>Interest (Year)</th>
          <th>Total Interest</th>
          <th>Invested Capital</th>
        </tr>
      </thead>
      <tbody>
        {props.data.map((yearData) => (
          <tr key={yearData.year}>
            <td>{yearData.year}</td>                                  {/* NO OF YEAR*/}
            <td>{formatter.format(yearData.savingsEndOfYear)}</td>    {/* TOTAL SAVINGS END OF YEAR*/}
            <td>{formatter.format(yearData.yearlyInterest)}</td>      {/* INTEREST GAINED IN YEAR */}
            <td>
              {formatter.format(yearData.savingsEndOfYear -
                props.initialInvestment -
                yearData.yearlyContribution * yearData.year)}
            </td>                                                      {/* TOTAL INTEREST GAINED */}
            <td>
              {formatter.format(props.initialInvestment -
                yearData.yearlyContribution * yearData.year)}
            </td>                                                       {/* TOTAL INVESTED CAPITAL */}
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default ResultsTable;
